    def press_save_button(self):


        # create or connect to a database
        conn = sqlite3.connect('BSCsettings.db')
        # Create a cursor
        c = conn.cursor()
        # Delete everything in the database table
        c.execute('DELETE FROM sniper_bnb_settings;',)
        walletlist = self.wallet_input.text()
        keylist = self.key_input.text()
        nodelist = self.node_input.text()
        gaslimit = self.gas_limit_input.text()
        totallist = [walletlist,keylist,nodelist,gaslimit]
        c.execute("INSERT INTO sniper_bnb_settings (wallet, key, node,gaslimit) VALUES (?,?,?,?)",(totallist[0],totallist[1],totallist[2],totallist[3]))
        # Commit the changes
        conn.commit()
        # Close the connection
        conn.close()
        self.tx_viewer.setText("Saved Settings")


